CREATE OR REPLACE FUNCTION obter_dados_interface ( cd_interface_p bigint, ds_objeto_p text, nr_seq_cobr_escrit_p bigint, ie_condisao_p text) RETURNS varchar AS $body$
DECLARE


nr_sequencia_w		bigint;
nm_inteface_w		varchar(255);
ds_view_w		varchar(50);
nm_tabela_w		varchar(255);
ds_retorno_w		varchar(255);
nr_seq_conta_banco_w	bigint;


BEGIN
select	max(b.nr_seq_conta_banco)
into STRICT	nr_seq_conta_banco_w
from	cobranca_escritural	b,
	cobranca_escrit_log_arq a
where	a.nr_seq_cobr_escrit = b.nr_sequencia
and	a.nr_seq_cobr_escrit = nr_seq_cobr_escrit_p;

select 	a.nr_sequencia cd,
        coalesce(a.nm_exibicao, b.ds_interface) ds,
        a.ds_view,
        a.nm_tabela
into STRICT	nr_sequencia_w,
	nm_inteface_w,
	ds_view_w,
	nm_tabela_w
from    interface b,
        banco_estab_interf a
where   a.cd_interface = b.cd_interface
and	b.cd_interface = cd_interface_p
and     a.nr_seq_conta_banco = nr_seq_conta_banco_w
and     a.ds_objeto = upper(ds_objeto_p)
order by ds;

if (ie_condisao_p = 'NR') then
	ds_retorno_w := nr_sequencia_w;
elsif (ie_condisao_p = 'NM') then
	ds_retorno_w := nm_inteface_w;
elsif (ie_condisao_p = 'V') then
	ds_retorno_w := ds_view_w;
elsif (ie_condisao_p = 'T') then
	ds_retorno_w := nm_tabela_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
