CREATE OR REPLACE FUNCTION obter_dados_interf_gif_sp ( dt_inicial_p timestamp, dt_final_p timestamp, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(2000) := '';
nr_nota_fiscal_w	varchar(255);


/*ie_opcao_p

'C' - canceladas	Sit. 9
'E' - Estornadas	Sit. 3
'R' - Retidas		Tem ISS
'T' - Todas		Sit. 1
*/
c01 CURSOR FOR
SELECT	distinct(a.nr_nota_fiscal)
from	nota_fiscal a
where	a.dt_emissao between dt_inicial_p and fim_dia(dt_final_p)
and	((a.ie_situacao = CASE WHEN ie_opcao_p='C' THEN 9 WHEN ie_opcao_p='E' THEN 3 WHEN ie_opcao_p='T' THEN 1 END )
	 or (ie_opcao_p = 'R' and exists (select 1
						  from	   tributo c,
							   nota_fiscal_trib b
						  where   b.cd_tributo	= c.cd_tributo
						  and     c.ie_tipo_tributo	= 'ISS'
						  and     a.nr_sequencia	= b.nr_sequencia)))
order by 1;


BEGIN

open c01;
loop
fetch c01 into
	nr_nota_fiscal_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	ds_retorno_w := substr(ds_retorno_w || nr_nota_fiscal_w || ',',1,2000);

end loop;
close c01;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
