CREATE OR REPLACE FUNCTION obter_dados_interr_cirurgia (nr_seq_motivo_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);

/*ie_opção p M = Motivo,   G = Grupo do Motivo*/

BEGIN
if (nr_seq_motivo_p IS NOT NULL AND nr_seq_motivo_p::text <> '') and (ie_opcao_p = 'M') then
	select          ds_motivo_interrupcao
	into STRICT 		ds_retorno_w
	from     	motivo_interrup_cirurgia
	where 		nr_sequencia = nr_seq_motivo_p;
end if;

if (nr_seq_motivo_p IS NOT NULL AND nr_seq_motivo_p::text <> '') and (ie_opcao_p = 'G') then
	select          ds_grupo_interrupcao
	into STRICT 		ds_retorno_w
	from     	motivo_interrup_cirurgia a,
			grupo_interrupcao_cirurgia b
	where 		b.nr_sequencia 	= a.nr_seq_grupo
	and 		a.nr_sequencia 	= nr_seq_motivo_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
