CREATE OR REPLACE FUNCTION obter_dados_intervalo_prescr ( cd_intervalo_p text, ie_opcao_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);

/*
***Opções:
 C  	- cd_intervalo
 DH 	- ds_horarios
 L	- limpar horários
 MA 	- qt_min_agora
 PD	- ie_prescricao_dieta
 QD 	- ie_questiona
 QO	- qt_operacao
 SN	- ie_questionar_dia
 OP     - Operação
 */
BEGIN
if (ie_opcao_p = 'C') then
	select	max(cd_intervalo)
	into STRICT	ds_retorno_w
	from	intervalo_prescricao
	where	ie_agora	= 'S'
	and	ie_situacao	= 'A';
elsif (ie_opcao_p = 'DH') then
	select	ds_horarios
	into STRICT	ds_retorno_w
	from	intervalo_prescricao
	where	cd_intervalo = cd_intervalo_p;
elsif (ie_opcao_p = 'L') then
	select	coalesce(max(ie_limpar_horarios),'N')
	into STRICT	ds_retorno_w
	from	intervalo_prescricao
	where	cd_intervalo = cd_intervalo_p;
elsif (ie_opcao_p = 'MA') then
	select	coalesce(qt_min_agora,0)
	into STRICT	ds_retorno_w
	from	intervalo_prescricao
	where	cd_intervalo = cd_intervalo_p;
elsif (ie_opcao_p = 'PD') then
	select	coalesce(max(ie_prescricao_dieta),'N')
	into STRICT	ds_retorno_w
	from	intervalo_prescricao
	where	cd_intervalo = cd_intervalo_p;
elsif (ie_opcao_p = 'QD') then
	select	coalesce(ie_questionar_dia,'N')
	into STRICT	ds_retorno_w
	from	intervalo_prescricao
	where	cd_intervalo = cd_intervalo_p;
elsif (ie_opcao_p = 'QO') then
	select	coalesce(max(qt_operacao),1)
	into STRICT	ds_retorno_w
	from	intervalo_prescricao
	where	cd_intervalo = cd_intervalo_p;
elsif (ie_opcao_p = 'SN') then
	select	coalesce(max(qt_se_necessario),1)
	into STRICT	ds_retorno_w
	from	intervalo_prescricao
	where	cd_intervalo = cd_intervalo_p;
elsif (ie_opcao_p = 'OP') then
	select	coalesce(max(ie_operacao),1)
	into STRICT	ds_retorno_w
	from	intervalo_prescricao
	where	cd_intervalo = cd_intervalo_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
