CREATE OR REPLACE FUNCTION obter_dados_inter_prescr ( nr_prescricao_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

select	CASE WHEN coalesce(ie_higienizacao,'S')='S' THEN 'Higienização' END  || ' ' ||
	CASE WHEN coalesce(ie_preparo,'S')='S' THEN 'Preparo' END  || ' ' ||
	CASE WHEN coalesce(ie_conferencia,'S')='S' THEN 'Conferência' END
into STRICT	ds_retorno_w
from	prescr_material
where	nr_prescricao = nr_prescricao_p
and	nr_sequencia = nr_sequencia_p;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
