SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_int_obstetrica (nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
'1' ¿ Internação Obstétrica / Em gestação
'2' ¿ Internação Obstétrica / Aborto
'3' - Internação Obstétrica / Transtorno materno relacionado a gravidez
'4' - Internação Obstétrica / Complic. Puerpério
'5' - Internação Obstétrica / Atend. Ao RN na sala de parto
'6' - Internação Obstétrica / Complicação Neonatal
'7' - Internação Obstétrica / Bx Peso < 2,5 kg
'8' - Internação Obstétrica / Parto Cesáreo
'9' - Internação Obstétrica / Parto Normal
*/
ds_retorno_w		varchar(1):= 'N';
qt_complicacoes_w	bigint:= 0;
qt_peso_w		smallint:= 0;


BEGIN

if (ie_opcao_p = '1') then

	select 	max(CASE WHEN ie_tipo_nascimento=1 THEN 'S'  ELSE 'N' END )
	into STRICT	ds_retorno_w
	from 	nascimento
	where 	nr_atendimento = nr_atendimento_p;

elsif (ie_opcao_p = '2') then

	select 	max(CASE WHEN ie_tipo_nascimento=2 THEN 'S'  ELSE 'N' END )
	into STRICT	ds_retorno_w
	from 	nascimento
	where 	nr_atendimento = nr_atendimento_p;

elsif (ie_opcao_p = '3') then

	select 	max(CASE WHEN ie_tipo_nascimento=6 THEN 'S'  ELSE 'N' END )
	into STRICT	ds_retorno_w
	from 	nascimento
	where 	nr_atendimento = nr_atendimento_p;

elsif (ie_opcao_p = '4') then

	select 	count(*)
	into STRICT	qt_complicacoes_w
	from 	parto_eventos
	where 	nr_atendimento = nr_atendimento_p;

	if (qt_complicacoes_w > 0) then
		ds_retorno_w:=	'S';
	else
		ds_retorno_w:=	'N';
	end if;

elsif (ie_opcao_p = '5') then

	select 	max(CASE WHEN coalesce(cd_pediatra::text, '') = '' THEN 'N'  ELSE 'S' END )
	into STRICT	ds_retorno_w
	from 	nascimento
	where 	nr_atendimento = nr_atendimento_p;

elsif (ie_opcao_p = '6') then

	select 	count(*)
	into STRICT	qt_complicacoes_w
	from 	nascimento_evento
	where 	nr_atendimento = nr_atendimento_p;

	if (qt_complicacoes_w > 0) then
		ds_retorno_w:=	'S';
	else
		ds_retorno_w:=	'N';
	end if;

elsif (ie_opcao_p = '7') then

	select 	max(qt_peso)
	into STRICT	qt_peso_w
	from 	nascimento
	where 	nr_atendimento = nr_atendimento_p;

	if (qt_peso_w < 2.5) then
		ds_retorno_w:= 'S';
	else
		ds_retorno_w:= 'N';
	end if;

elsif (ie_opcao_p = '8') then

	select 	CASE WHEN coalesce(ie_parto_cesaria::text, '') = '' THEN 'N'  ELSE ie_parto_cesaria END
	into STRICT	ds_retorno_w
	from 	parto
	where  	nr_atendimento = nr_atendimento_p;

elsif (ie_opcao_p = '9') then

	select  CASE WHEN coalesce(ie_parto_normal::text, '') = '' THEN 'N'  ELSE ie_parto_normal END
	into STRICT	ds_retorno_w
	from	parto
	where	nr_atendimento = nr_atendimento_p;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_int_obstetrica (nr_atendimento_p bigint, ie_opcao_p text) FROM PUBLIC;

