SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_isolamento (nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_motivo_w			bigint;
ds_motivo_w			varchar(100);
nr_seq_motivo_atend_w		bigint;
nr_seq_motivo_atend_f_w		bigint;
nr_seq_motivo_isol_w		bigint;
dt_inicio_isolamento_w		timestamp;
dt_fim_isolamento_w		timestamp;
qt_dias_w			bigint;
ds_motivo_isol_cih_w		varchar(255);


BEGIN

select	coalesce(max(nr_Sequencia),0)
into STRICT	nr_seq_motivo_atend_w
from	motivo_isolamento_atend
where	nr_atendimento = nr_atendimento_p;

select	coalesce(max(nr_Sequencia),0)
into STRICT	nr_seq_motivo_atend_f_w
from	motivo_isolamento_atend
where	nr_atendimento = nr_atendimento_p
and	coalesce(dt_fim_isolamento::text, '') = '';

if	(nr_seq_motivo_atend_w > 0 AND ie_opcao_p <> 'DSF') then
	select  nr_seq_motivo_isol,
		dt_inicio_isolamento,
		dt_fim_isolamento,
		coalesce(obter_dias_entre_datas(dt_inicio_isolamento,coalesce(dt_fim_isolamento,clock_timestamp())),0),
		substr(obter_dados_tipo_isolamento(nr_seq_cih_tipo_isolamento,'D'),1,255)
	into STRICT	nr_seq_motivo_isol_w,
		dt_inicio_isolamento_w,
		dt_fim_isolamento_w,
		qt_dias_w,
		ds_motivo_isol_cih_w
	from	motivo_isolamento_atend
	where	nr_sequencia = CASE WHEN nr_seq_motivo_atend_f_w=0 THEN nr_seq_motivo_atend_w  ELSE nr_seq_motivo_atend_f_w END;

	select  ds_motivo
	into STRICT	ds_motivo_w
	from	motivo_isolamento
	where	nr_sequencia = nr_seq_motivo_isol_w;
end if;

if	(nr_seq_motivo_atend_f_w > 0 AND ie_opcao_p = 'DSF') then

	select substr(obter_dados_tipo_isolamento(nr_seq_cih_tipo_isolamento,'D'),1,255)
	into STRICT   ds_motivo_isol_cih_w
	from   motivo_isolamento_atend
	where	nr_sequencia = nr_seq_motivo_atend_w;
end if;

if (upper(ie_opcao_p) = 'C') then
	return	nr_seq_motivo_isol_w;
elsif (upper(ie_opcao_p) = 'I') then
	return	to_char(dt_inicio_isolamento_w,'dd/mm/yyyy hh24:mi:ss');
elsif (upper(ie_opcao_p) = 'F') then
	return	to_char(dt_fim_isolamento_w,'dd/mm/yyyy hh24:mi:ss');
elsif (upper(ie_opcao_p) = 'D') then
	return	to_char(qt_dias_w);
elsif (upper(ie_opcao_p) = 'DS') then
	return ds_motivo_isol_cih_w;
elsif (upper(ie_opcao_p) = 'DSF') then
	return ds_motivo_isol_cih_w;
else
	return ds_motivo_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
