CREATE OR REPLACE FUNCTION obter_dados_item_ageint ( nr_seq_agenda_cons_p bigint, nr_seq_agenda_exame_p bigint, nr_seq_radioterapia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_Retorno_w	varchar(255);
/*
IE_OPCAO_P
C = Classificação
*/
BEGIN

if (ie_opcao_p = 'C') then
	if (nr_seq_Agenda_exame_p IS NOT NULL AND nr_seq_Agenda_exame_p::text <> '') then
		select	max(substr(obter_desc_classif_agenda_pac(nr_seq_classif_agenda),1,255))
		into STRICT	ds_Retorno_w
		from	agenda_paciente
		where	nr_sequencia	= nr_seq_Agenda_exame_p;
	elsif (nr_seq_Agenda_cons_p IS NOT NULL AND nr_seq_Agenda_cons_p::text <> '') then
		select	max(substr(obter_classif_agenda_consulta(ie_classif_agenda),1,255))
		into STRICT	ds_Retorno_w
		from	agenda_consulta
		where	nr_sequencia	= nr_seq_Agenda_cons_p;
	else
		select 	max(substr(rxt_obter_desc_classif_agenda(ie_classif_agenda),1,255))
		into STRICT	ds_retorno_w
		from	rxt_agenda
		where	nr_sequencia	= nr_seq_radioterapia_p;
	end if;
end if;

return	ds_Retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
