SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_item_conv (cd_convenio_p bigint, cd_categoria_p text, ie_tipo_item_p bigint, cd_item_p bigint, ie_origem_proced_p bigint, ie_estrutura_completa_p text, cd_especialidade_med_p bigint, cd_medico_p text, ie_tipo_atendimento_p bigint, nr_atendimento_p bigint, nr_seq_proc_interno_p bigint, nr_seq_exame_p bigint, ie_cod_desc_p text) RETURNS varchar AS $body$
DECLARE


/* tipo do item    1-procedimentos/servicos 2-materiais/medicamentos */


/* estrutura completa s-sim (validade toda estrutura) n-nao (valida so proc) */


/* ie_cod_desc_p 
	C - Codigo do item no Convenio
	D - Descricao do item no Convenio
	M - Tipo do material no Convenio
*/
cd_codigo_convenio_w	varchar(20);
ds_item_convenio_w		varchar(240);
cd_grupo_proc_w			conversao_proc_convenio.cd_grupo_proced%type;
cd_espec_proc_w			conversao_proc_convenio.cd_especial_proced%type;
cd_area_proc_w			conversao_proc_convenio.cd_area_proced%type;
cd_grupo_mat_w			smallint;
cd_subgrupo_mat_w		smallint;
cd_classe_mat_w			integer;
ie_tipo_mat_w			varchar(03);
cd_especial_medica_w	integer;
ie_tipo_atendimento_w	integer;
cd_setor_atendimento_w	bigint;
ds_item_w				varchar(240);
dt_procedimento_w		timestamp;
dia_semana_w			smallint		:= 0;
dia_feriado_w			smallint		:= 0;
ie_feriado_w			varchar(1)		:= 'N';
cd_estabelecimento_w	smallint;
nr_sequencia_w			bigint;
nr_seq_proc_pacote_w	bigint;
nr_seq_pacote_w			bigint;
ie_tipo_material_conv_w	conversao_material_convenio.ie_tipo_material_conv%type;
ie_clinica_w		atendimento_paciente.ie_clinica%type;


c01 CURSOR FOR
	SELECT		cd_proc_convenio,
			coalesce(ds_proc_convenio,ds_item_w)
	from		conversao_proc_convenio
	where		cd_convenio					= cd_convenio_p
	and		coalesce(cd_categoria, coalesce(cd_categoria_p,'0')) = coalesce(cd_categoria_p,'0')
	and		coalesce(cd_procedimento,coalesce(cd_item_p,0)) 			= coalesce(cd_item_p,0)
	and		coalesce(ie_origem_proced,coalesce(ie_origem_proced_p,0))	= coalesce(ie_origem_proced_p,0)
	and		coalesce(cd_area_proced,coalesce(cd_area_proc_w,0))		= coalesce(cd_area_proc_w,0)
	and		coalesce(cd_especial_proced,coalesce(cd_espec_proc_w,0))		= coalesce(cd_espec_proc_w,0)
	and		coalesce(cd_grupo_proced,coalesce(cd_grupo_proc_w,0))		= coalesce(cd_grupo_proc_w,0)
	and 		coalesce(nr_seq_proc_interno, coalesce(nr_seq_proc_interno_p,0)) = coalesce(nr_seq_proc_interno_p,0)
	and		coalesce(nr_seq_exame, coalesce(nr_seq_exame_p,0))	= coalesce(nr_seq_exame_p,0)
	and		ie_situacao					= 'A'
	and (coalesce(cd_especialidade_medica, coalesce(cd_especial_medica_w,0)) 	= coalesce(cd_especial_medica_w,0))
	and (coalesce(ie_tipo_atendimento, coalesce(ie_tipo_atendimento_w,0))    	= coalesce(ie_tipo_atendimento_w,0))
	and (coalesce(cd_setor_atendimento, coalesce(cd_setor_atendimento_w,0))    	= coalesce(cd_setor_atendimento_w,0))
	and (coalesce(cd_estabelecimento, coalesce(cd_estabelecimento_w,1)) = coalesce(cd_estabelecimento_w,1))
	and		((coalesce(upper(ie_feriado),'A') = 'A') or (upper(ie_feriado) = ie_feriado_w))
	and		coalesce(nr_seq_pacote, coalesce(nr_seq_pacote_w,0)) = coalesce(nr_seq_pacote_w,0)
	and		coalesce(ie_clinica, coalesce(ie_clinica_w,0)) = coalesce(ie_clinica_w,0)
	and		((coalesce(dt_procedimento_w::text, '') = '')
		or (dt_procedimento_w between coalesce(dt_inicio_vigencia, dt_procedimento_w)
		and coalesce(dt_vigencia_final, dt_procedimento_w) + 86399/89400))
	and	((coalesce(dt_dia_semana, dia_semana_w) = dia_semana_w)
		or (dt_dia_semana = 9))
	and 	(((coalesce(hr_final::text, '') = '') or (coalesce(hr_inicial::text, '') = '')) or (coalesce(dt_procedimento_w,clock_timestamp()) between
				to_date(to_char(coalesce(dt_procedimento_w,clock_timestamp()),'dd/mm/yyyy') || ' ' ||
					coalesce(to_char(hr_inicial,'hh24:mi:ss'), '00:00:01'), 
					'dd/mm/yyyy hh24:mi:ss')
						and
				to_date(to_char(coalesce(dt_procedimento_w,clock_timestamp()),'dd/mm/yyyy') || ' ' ||
					coalesce(to_char(hr_final,'hh24:mi:ss'), '23:59:59'), 
					'dd/mm/yyyy hh24:mi:ss')))
	order by
			coalesce(cd_procedimento,0),
			coalesce(cd_grupo_proced,0),
			coalesce(cd_especial_proced,0),
			coalesce(cd_area_proced,0),
			coalesce(ie_clinica,0),
			coalesce(cd_categoria,' ');
			

c02 CURSOR FOR
	SELECT		cd_material_convenio,
			coalesce(ds_material_convenio,ds_item_w),
				coalesce(ie_tipo_material_conv, ie_tipo_mat_w)
	from		conversao_material_convenio
	where		cd_convenio					= cd_convenio_p
	and		coalesce(cd_categoria, coalesce(cd_categoria_p,'0')) = coalesce(cd_categoria_p,'0')
	and		coalesce(cd_material,cd_item_p) 			= cd_item_p
	and		coalesce(cd_grupo_material,cd_grupo_mat_w) 		= cd_grupo_mat_w
	and		coalesce(cd_subgrupo_material,cd_subgrupo_mat_w) 	= cd_subgrupo_mat_w
	and		coalesce(cd_classe_material,cd_classe_mat_w) 	= cd_classe_mat_w
	and		coalesce(ie_tipo_material,ie_tipo_mat_w) 		= ie_tipo_mat_w
	and		coalesce(ie_clinica, coalesce(ie_clinica_w,0))		= coalesce(ie_clinica_w,0)
	and 		ie_situacao					= 'A'
	order by
			coalesce(cd_material,0),
			coalesce(cd_grupo_material,0),
			coalesce(cd_subgrupo_material,0),
			coalesce(cd_classe_material,0),
			coalesce(ie_tipo_material,'0'),
			coalesce(ie_clinica,0),
			coalesce(cd_categoria_p,' ');


BEGIN

SELECT 	wheb_usuario_pck.get_cd_estabelecimento
into STRICT	cd_estabelecimento_w
;

cd_codigo_convenio_w		:= cd_item_p;
cd_especial_medica_w		:= coalesce(cd_especialidade_med_p, 0);
ie_tipo_atendimento_w		:= coalesce(ie_tipo_atendimento_p, 0);
nr_seq_pacote_w				:= 0;

if (ie_tipo_item_p	= 1) then
	begin
	/* busca estrutura procedimento */

	if (ie_estrutura_completa_p	= 'N') then
		begin
		cd_grupo_proc_w	:= 99999999;
		cd_espec_proc_w	:= 99999999;
		cd_area_proc_w	:= 99999999;
		end;
	else
		begin
		select 	cd_grupo_proc,
			cd_especialidade,
			cd_area_procedimento
		into STRICT	cd_grupo_proc_w,
			cd_espec_proc_w,
			cd_area_proc_w
		from	estrutura_procedimento_v
		where	cd_procedimento 	= cd_item_p
		and	ie_origem_proced	= ie_origem_proced_p;
		exception
     				when others then
				cd_grupo_proc_w	:= 0;
		end;
	end if;
	
	select	max(ie_clinica)
	into STRICT	ie_clinica_w
	from	atendimento_paciente
	where	nr_atendimento = nr_atendimento_p;

	cd_setor_atendimento_w	:= 0;
	
	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_sequencia_w
	from	procedimento_paciente
	where	nr_atendimento		= nr_atendimento_p
	and	cd_procedimento		= cd_item_p
	and	ie_origem_proced	= ie_origem_proced_p
	and	coalesce(cd_motivo_exc_conta::text, '') = '';
	
	
	
	if (nr_sequencia_w > 0) then
		
		select	max(cd_setor_atendimento),
				max(dt_procedimento),
				max(nr_seq_proc_pacote)
		into STRICT	cd_setor_atendimento_w,
				dt_procedimento_w,
				nr_seq_proc_pacote_w
		from	procedimento_paciente
		where	nr_sequencia = nr_sequencia_w;
		
		if (coalesce(nr_seq_proc_pacote_w,0) > 0) then
			select  coalesce(max(nr_seq_pacote),0)
			into STRICT	nr_seq_pacote_w
			from 	atendimento_pacote
			where 	nr_seq_procedimento = nr_seq_proc_pacote_w
			and 	nr_atendimento = nr_atendimento_p;
		end if;
		
		dia_semana_w := pkg_date_utils.get_weekday(dt_procedimento_w);

		select 	count(*)
		into STRICT 	dia_feriado_w
		from	feriado
		where 	pkg_date_utils.start_of(dt_feriado,'DD',0) = pkg_date_utils.start_of(dt_procedimento_w,'DD',0);

		if (dia_feriado_w > 0) then
			ie_feriado_w:= 'S';
		else
			ie_feriado_w:= 'N';
		end if;
	
	end if;

	
	
	/* busca descricao do procedimento para o convenio */

	begin
	open c01;
	loop
	fetch c01 into
		cd_codigo_convenio_w,
		ds_item_convenio_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		cd_codigo_convenio_w	:= cd_codigo_convenio_w;
		ds_item_convenio_w	:= ds_item_convenio_w;
		end;
	end loop;
	close c01;
	end;
	end;
else
	begin
	/* busca estrutura material */

	begin
	select 	cd_grupo_material,
		cd_subgrupo_material,
		cd_classe_material,
		ie_tipo_material
	into STRICT	cd_grupo_mat_w,
		cd_subgrupo_mat_w,
		cd_classe_mat_w,
		ie_tipo_mat_w
	from 	estrutura_material_v
	where 	cd_material		= cd_item_p;
	exception
    when others then
		cd_grupo_mat_w			:= 0;
	end;

	ds_item_convenio_w	:= ds_item_w;
	
	/* busca descricao do material para o convenio */

	begin
	open c02;
	loop
	fetch c02 into
		cd_codigo_convenio_w,
		ds_item_convenio_w,
		ie_tipo_material_conv_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		begin
		cd_codigo_convenio_w	:= cd_codigo_convenio_w;
		ds_item_convenio_w	:= ds_item_convenio_w;
		ie_tipo_material_conv_w := ie_tipo_material_conv_w;
		end;
	end loop;
	close c02;
	end;
	end;
end if;

if (ie_cod_desc_p = 'C') then
	return cd_codigo_convenio_w;
elsif (ie_cod_desc_p = 'D') then
	return ds_item_convenio_w;
elsif (ie_cod_desc_p = 'M') then
	return ie_tipo_material_conv_w;
end if;
	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_item_conv (cd_convenio_p bigint, cd_categoria_p text, ie_tipo_item_p bigint, cd_item_p bigint, ie_origem_proced_p bigint, ie_estrutura_completa_p text, cd_especialidade_med_p bigint, cd_medico_p text, ie_tipo_atendimento_p bigint, nr_atendimento_p bigint, nr_seq_proc_interno_p bigint, nr_seq_exame_p bigint, ie_cod_desc_p text) FROM PUBLIC;

