CREATE OR REPLACE FUNCTION obter_dados_item_proj_recurso ( nr_seq_proj_rec_p bigint, cd_material_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE

/*ie_opcao_p
Q - Quantidade
V - Valor*/
qt_material_w		double precision;
vl_unitario_w		double precision;
vl_retorno_w		double precision;


BEGIN
select	max(qt_material),
	max(vl_unitario)
into STRICT	qt_material_w,
	vl_unitario_w
from	projeto_recurso_item
where	nr_seq_proj_rec = nr_seq_proj_rec_p
and	cd_material = cd_material_p;

if (ie_opcao_p = 'Q') then
	vl_retorno_w := qt_material_w;
elsif (ie_opcao_p = 'V') then
	vl_retorno_w := vl_unitario_w;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
