SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_itens_autor_pls ( nr_seq_item_p bigint, ie_proc_mat_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
qt_autorizada_w	bigint;
ds_status_pls_w	varchar(220);

/*
nr_seq_item_p = item material autorizado ou procedimento autorizado
ie_proc_mat_p = 'M' Material 'P' - Procedimento
ie_opcao_p
S - desc status OPS
Q - Qt autor OPS

*/
BEGIN

if (ie_proc_mat_p = 'P') then
	begin
	select	substr(obter_valor_dominio(1832,ie_status),1,200),
		qt_autorizada
	into STRICT	ds_status_pls_w,
		qt_autorizada_w
	from	pls_guia_plano_proc
	where	nr_seq_proc_autor = nr_seq_item_p;
	exception
	when others then
		ds_status_pls_w	:= null;
		qt_autorizada_w	:= 0;
	end;

elsif (ie_proc_mat_p = 'M') then
	begin
	select	substr(obter_valor_dominio(1832,ie_status),1,200),
		qt_autorizada
	into STRICT	ds_status_pls_w,
		qt_autorizada_w
	from	pls_guia_plano_mat
	where	nr_seq_mat_autor = nr_seq_item_p;
	exception
	when others then
		ds_status_pls_w	:= null;
		qt_autorizada_w	:= 0;
	end;
end if;

if (ie_opcao_p = 'S') then

	ds_retorno_w := ds_status_pls_w;
elsif (ie_opcao_p = 'Q') then

	ds_retorno_w := qt_autorizada_w;
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
