SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_kit_estoque ( nr_sequencia_p bigint, ie_retorno_p text) RETURNS varchar AS $body$
DECLARE


/*ie_retorno_p
'CR' = Nr Cirurgia
CK = Código do kit material
DL = Data de liberação da solicitação do kit*/
nr_cirurgia_w		bigint;
cd_kit_material_w		bigint;
ds_retorno_w		varchar(255);
nr_seq_solic_kit_w		bigint;


BEGIN
begin
select	nr_cirurgia,
	cd_kit_material,
	nr_seq_solic_kit
into STRICT	nr_cirurgia_w,
	cd_kit_material_w,
	nr_seq_solic_kit_w
from	kit_estoque
where	nr_sequencia = nr_sequencia_p;
exception
when others then
	begin
	nr_cirurgia_w		:= null;
	cd_kit_material_w	:= null;
	nr_seq_solic_kit_w	:= null;
	end;
end;

if (ie_retorno_p = 'CR') then
	ds_retorno_w := nr_cirurgia_w;
elsif (ie_retorno_p = 'CK') then
	ds_retorno_w := cd_kit_material_w;
elsif (ie_retorno_p = 'DL') and (nr_seq_solic_kit_w IS NOT NULL AND nr_seq_solic_kit_w::text <> '')then
	select	to_char(dt_liberacao, 'dd/mm/yyyy')
	into STRICT	ds_retorno_w
	from	solic_kit_material
	where	nr_sequencia = nr_seq_solic_kit_w;
end if;

return	ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
