CREATE OR REPLACE FUNCTION obter_dados_label (nm_atributo_p text, nm_tabela_p text) RETURNS varchar AS $body$
DECLARE


ds_label_w	varchar(60);


BEGIN

	select	coalesce(substr(obter_desc_expressao(cd_exp_label),1,254),substr(obter_desc_expressao(cd_exp_label_grid),1,254))
	into STRICT	ds_label_w
	from	tabela_atributo
	where	nm_tabela	= nm_tabela_p
	and	nm_atributo	= nm_atributo_p;

		if (coalesce(ds_label_w,'X') = 'X') then

			select max(coalesce(substr(obter_desc_expressao(cd_exp_label),1,254),substr(obter_desc_expressao(cd_exp_label_grid),1,254)))
			into STRICT	ds_label_w
			from tabela_visao_atributo
			where nm_atributo = nm_atributo_p;

		end if;

return	ds_label_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
