CREATE OR REPLACE FUNCTION obter_dados_lab_parametro (ie_tipo_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w  varchar(255) := null;


BEGIN

if (ie_tipo_p = 'CC') then --Codigo Contrato
	select  max(cd_contrato_fleury)
	into STRICT    ds_retorno_w
	from    lab_parametro
	where   cd_estabelecimento = cd_estabelecimento_p;

	if (coalesce(ds_retorno_w::text, '') = '') then

		select  max(cd_contrato_fleury)
		into STRICT    ds_retorno_w
		from    lab_parametro;

	end if;


elsif (ie_tipo_p = 'CF') then --Codigo unidade fleury
	select  max(cd_unidade_fleury)
	into STRICT    ds_retorno_w
	from    lab_parametro
	where   cd_estabelecimento = cd_estabelecimento_p;

	if (coalesce(ds_retorno_w::text, '') = '') then

		select  max(cd_unidade_fleury)
		into STRICT    ds_retorno_w
		from    lab_parametro;

	end if;

elsif (ie_tipo_p = 'AF') then --Agrupa ficha fleury
	select	coalesce(max(ie_agrupa_ficha_fleury),'N')
	into STRICT	ds_retorno_w
	from	lab_parametro
	where	cd_estabelecimento = cd_estabelecimento_p;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
