SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_laudo_paciente ( nr_seq_laudo_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(4000) := '';


/* TOMAR CUIDADO AO UTILIZAR ESTA FUNCTION POIS O DS_LAUDO É UM CAMPO LONG, E PODE ESTOURAR A VARIÁVEL*/

BEGIN

if (nr_seq_laudo_p > 0)  then

	if (ie_opcao_p = 'L') then
		
		select	ds_laudo
		into STRICT	ds_retorno_w
		from	laudo_paciente
		where	nr_sequencia	= nr_seq_laudo_p;
	
	elsif (ie_opcao_p = 'T') then
	
		select	ds_titulo_laudo
		into STRICT	ds_retorno_w
		from	laudo_paciente
		where	nr_sequencia	= nr_seq_laudo_p;
	
	elsif (ie_opcao_p = 'PR') then
	
		select	nr_prescricao
		into STRICT	ds_retorno_w
		from	laudo_paciente
		where	nr_sequencia	= nr_seq_laudo_p;
		
	elsif (ie_opcao_p = 'LD') then
	
		select	nr_laudo
		into STRICT	ds_retorno_w
		from	laudo_paciente
		where	nr_sequencia	= nr_seq_laudo_p;

	elsif (ie_opcao_p = 'DT') then

		select	to_char(dt_laudo,'dd/mm/yyyy')
		into STRICT	ds_retorno_w
		from	laudo_paciente
		where	nr_sequencia	= nr_seq_laudo_p;
		
	elsif (ie_opcao_p = 'S')	then
	
		select	obter_status_laudo(nr_sequencia)
		into STRICT	ds_retorno_w
		from	laudo_paciente
		where	nr_sequencia	= nr_seq_laudo_p;

	elsif (ie_opcao_p = 'F') then

	       select coalesce(ie_formato,'1')
	       into STRICT ds_retorno_w
	       from laudo_paciente
	       where nr_sequencia = nr_seq_laudo_p;

	elsif (ie_opcao_p = 'A') then

	       select ds_arquivo
	       into STRICT ds_retorno_w
	       from laudo_paciente
	       where nr_sequencia = nr_seq_laudo_p;
	
	end if;

end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_laudo_paciente ( nr_seq_laudo_p bigint, ie_opcao_p text) FROM PUBLIC;

