CREATE OR REPLACE FUNCTION obter_dados_leitura_evento ( nr_seq_evento_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ie_status_w	varchar(15);
ds_retorno_w	varchar(255);


BEGIN

select	max(b.ie_status)
into STRICT	ie_status_w
from	ev_evento_paciente a,
	ev_evento_pac_destino b
where	b.nr_seq_ev_pac = a.nr_sequencia
and	a.nr_sequencia = nr_seq_evento_p
and	b.cd_pessoa_fisica = cd_pessoa_fisica_p;

if (ie_opcao_p = 'S') then
	ds_retorno_w	:= ie_status_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
