CREATE OR REPLACE FUNCTION obter_dados_lib_mat_pac (nr_prescricao_p bigint, nr_seq_prescricao_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


qt_retorno_w		smallint;
qt_dose_diaria_w	smallint;
qt_dose_tratamento_w	smallint;


BEGIN

if (coalesce(nr_prescricao_p,0) > 0) and (coalesce(nr_seq_prescricao_p,0) > 0) then

	select	max(qt_dose_diaria),
		max(qt_dias_tratamento)
	into STRICT	qt_dose_diaria_w,
		qt_dose_tratamento_w
	from	lib_material_paciente
	where	nr_prescricao  =  nr_prescricao_p
	and	nr_seq_material = nr_seq_prescricao_p;

	if (ie_opcao_p = 'DD') then
		qt_retorno_w := qt_dose_diaria_w;
	elsif (ie_opcao_p = 'DT') then
		qt_retorno_w := qt_dose_tratamento_w;
	end if;

end if;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
