CREATE OR REPLACE FUNCTION obter_dados_licitacao ( ie_opcao_p text, nr_seq_lic_p bigint) RETURNS varchar AS $body$
DECLARE


cd_processo_w		varchar(50);
ds_objeto_w		varchar(255);
dt_abertura_real_w		timestamp;


BEGIN
select	cd_processo,
	substr(ds_objeto,1,255),
	dt_abertura_real
into STRICT	cd_processo_w,
	ds_objeto_w,
	dt_abertura_real_w
from	lic_licitacao
where	nr_sequencia = nr_seq_lic_p;
if (ie_opcao_p = 'CD')	then
	return	cd_processo_w;
elsif (ie_opcao_p = 'OB')	then
	return	ds_objeto_w;
elsif (ie_opcao_p = 'DT')	then
	return	to_char(dt_abertura_real_w);
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
