SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_local_trat ( nr_seq_atendimento_p bigint, ie_opcap_p text) RETURNS varchar AS $body$
DECLARE


ds_cor_w	varchar(15) := null;


BEGIN

select	max(b.ds_cor)
into STRICT	ds_cor_w
from	agenda_quimio a,
	qt_local b
where	a.nr_seq_local		= b.nr_sequencia
and	a.nr_seq_atendimento	= nr_seq_atendimento_p
and 	a.ie_status_agenda <> 'C';

if (ie_opcap_p = 'C') then
	return	ds_cor_w;
else
	return 	null;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
