CREATE OR REPLACE FUNCTION obter_dados_loco_reg ( cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*

A	- Altura
CT	- Código da Topografia
DST	- Descrição da Topografia

*/
cd_topografia_w	varchar(10);
qt_altura_w	smallint;
qt_altura_ww	smallint;


BEGIN

begin
select	qt_altura
into STRICT	qt_altura_ww
from	paciente_setor
where	cd_pessoa_fisica	= cd_pessoa_fisica_p
and	nr_seq_paciente		= (	SELECT	max(nr_seq_paciente)
					from	paciente_setor
					where	cd_pessoa_fisica	= cd_pessoa_fisica_p);
exception
when others then
	qt_altura_ww	:= null;
end;

select	coalesce(qt_altura_ww, qt_altura),
	cd_topografia
into STRICT	qt_altura_w,
	cd_topografia_w
from	can_loco_regional
where	cd_pessoa_fisica	= cd_pessoa_fisica_p
and	nr_sequencia		= (	SELECT	max(nr_sequencia)
					from	can_loco_regional
					where	cd_pessoa_fisica	= cd_pessoa_fisica_p);
if (ie_opcao_p = 'A') then
	return	qt_altura_w;
elsif (ie_opcao_p = 'DST') then
	return	substr(obter_desc_cido_topografia(cd_topografia_w),1,100);
elsif (ie_opcao_p = 'CT') then
	return	cd_topografia_w;
end if;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
