SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_loc_laudo ( nr_seq_laudo_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE



/*
EP - Envelopado por
EE - Envelopado em
RP - Recebido por
RE - Recebido em
*/
ds_retorno_w		varchar(255);


BEGIN

if (upper(ie_opcao_p) = 'EP') then

	select	max(nm_usuario_nrec)
	into STRICT	ds_retorno_w
	from	laudo_paciente_loc
	where	ie_tipo_local = 4
	and	coalesce(dt_inativacao::text, '') = ''
	and	nr_Sequencia = nr_seq_laudo_p;

elsif (upper(ie_opcao_p) = 'EE') then

	select	to_char(max(dt_atualizacao_nrec),'dd/mm/yyyy hh24:mi:ss')
	into STRICT	ds_retorno_w
	from	laudo_paciente_loc
	where	ie_tipo_local = 4
	and	coalesce(dt_inativacao::text, '') = ''
	and	nr_Sequencia = nr_seq_laudo_p;

elsif (upper(ie_opcao_p) = 'RP') then

	select	max(nm_usuario_nrec)
	into STRICT	ds_retorno_w
	from	laudo_paciente_loc
	where	ie_tipo_local = 1
	and	coalesce(dt_inativacao::text, '') = ''
	and	nr_Sequencia = nr_seq_laudo_p;


elsif (upper(ie_opcao_p) = 'RE') then

	select	to_char(max(coalesce(DT_RETIRADA,dt_atualizacao_nrec)),'dd/mm/yyyy hh24:mi:ss')
	into STRICT	ds_retorno_w
	from	laudo_paciente_loc
	where	ie_tipo_local = 1
	and	coalesce(dt_inativacao::text, '') = ''
	and	nr_Sequencia = nr_seq_laudo_p;

end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_loc_laudo ( nr_seq_laudo_p bigint, ie_opcao_p text) FROM PUBLIC;

