SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_log_protocolo_conv ( nr_seq_protocolo_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/* 
U = Usu√°rio que alterou o status para Definitivo 
D = Data em que o status foi alterado para definitivo 
*/
 
						 
dt_atualizacao_w	varchar(21);
nm_usuario_w		varchar(60);
ds_retorno_w		varchar(60);
			

BEGIN 
 
select	to_char(max(l.dt_atualizacao),'dd/mm/yyyy hh24:mi:ss'), 
	max(l.nm_usuario) 
into STRICT	dt_atualizacao_w, 
	nm_usuario_w 
from	conta_paciente_hist l, 
	conta_paciente c 
where	l.nr_interno_conta 	= c.nr_interno_conta 
and	l.nr_nivel_anterior	<> 10 
and	l.nr_nivel_atual 	= 10 
and	c.nr_seq_protocolo 	= nr_seq_protocolo_p;
 
if (ie_opcao_p = 'U') then 
	 
	ds_retorno_w := nm_usuario_w;
else 
	ds_retorno_w := dt_atualizacao_w;
end if;
	 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
