CREATE OR REPLACE FUNCTION obter_dados_lote_cartao_cr ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w				varchar(4000);

BEGIN

if (coalesce(nr_sequencia_p,0) <> 0) then
	begin

	if (ie_opcao_p = 'DB') then
		select	substr(max(a.ds_bandeira),1,255)
		into STRICT	ds_retorno_w
		from	bandeira_cartao_cr a,
			lote_baixa_cartao_cr b
		where	a.nr_sequencia	= b.nr_seq_bandeira
		and	b.nr_sequencia	= nr_sequencia_p;
	end if;
	end;
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
