SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_lote_etapa (nr_seq_lote_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


ds_retorno_w   	double precision;
qt_contas_w   	bigint;
vl_conta_w   	double precision;


BEGIN

ds_retorno_w:= 0;

if (ie_opcao_p = 'Q') then

	SELECT 	COUNT(nr_interno_conta)
	INTO STRICT  	qt_contas_w
	FROM 	conta_paciente_etapa
	WHERE 	nr_seq_lote = nr_seq_lote_p;

	ds_retorno_w := qt_contas_w;

elsif (ie_opcao_p = 'V') then

	SELECT 	coalesce(SUM(vl_conta),0)
	INTO STRICT 	vl_conta_w
	FROM 	conta_paciente_etapa
	WHERE 	nr_seq_lote = nr_seq_lote_p;

	ds_retorno_w := vl_conta_w;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
