CREATE OR REPLACE FUNCTION obter_dados_lote_externo (nr_seq_lote_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


cd_cgc_w		pessoa_juridica.cd_cgc%type;
ds_razao_social_w	pessoa_juridica.ds_razao_social%type;
cd_interno_lab_w	pessoa_juridica_estab.cd_interno_lab%type;


BEGIN

select b.cd_cgc,
	b.ds_razao_social
into STRICT	cd_cgc_w,
	ds_razao_social_w
from	pessoa_juridica b,
	lab_lote_externo a
where a.cd_cgc = b.cd_cgc
  and a.nr_sequencia = nr_seq_lote_p;


select	max(b.cd_interno_lab) CodLab
into STRICT	cd_interno_lab_w
from	lab_lote_externo a,
	pessoa_juridica_estab b
where	b.cd_cgc = a.cd_cgc
and	a.nr_sequencia = nr_seq_lote_p
and 	b.cd_estabelecimento = coalesce(a.cd_estabelecimento,b.cd_estabelecimento);

if (ie_opcao_p = 1) then
	return cd_cgc_w;
elsif (ie_opcao_p = 2) then
	return ds_razao_social_w;
elsif (ie_opcao_p = 3) then
	return cd_interno_lab_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_lote_externo (nr_seq_lote_p bigint, ie_opcao_p bigint) FROM PUBLIC;

