SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_maquina_senha ( nm_maquina_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*
ie_opcao_p
NR - nr_Seq_computador
*/
nr_seq_maquina_w  		varchar(255);
cd_estabelecimento_w	smallint;



BEGIN

cd_estabelecimento_w := wheb_usuario_pck.get_cd_estabelecimento;

if (nm_maquina_p IS NOT NULL AND nm_maquina_p::text <> '') then

	if (ie_opcao_p = 'NR') then

		select	to_char(max(a.nr_seq_computador))
		into STRICT	nr_seq_maquina_w
		from	maquina_local_senha a,
				computador b
		where	a.nr_seq_computador	= b.nr_sequencia
		and		a.cd_estabelecimento 	= cd_estabelecimento_w
		and		nm_computador_pesquisa 	= padronizar_nome(upper(nm_maquina_p))
		and		coalesce(a.ie_situacao, 'A') = 'A';

	end if;
end if;

return nr_seq_maquina_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
