CREATE OR REPLACE FUNCTION obter_dados_marca_bolsa (nr_seq_marca_p bigint, ie_tipo_dados_p text) RETURNS varchar AS $body$
DECLARE


/*
DM	=	Descrição marca
DC	= 	Descrição material
*/
retorno_w			varchar(255);
ds_marca_w		varchar(60);
ds_material_w		varchar(255);

BEGIN

select 	ds_marca,
	coalesce(substr(obter_desc_material(cd_material),1,255),'Sem material informado') ds_material
into STRICT	ds_marca_w,
	ds_material_w
from	san_marca_bolsa
where	nr_sequencia	=	nr_seq_marca_p;


if (ie_tipo_dados_p = 'DM') then
	retorno_w		:=	ds_marca_w;
elsif (ie_tipo_dados_p = 'DC') then
	retorno_w		:=	ds_material_w;
end if;

return	retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
