CREATE OR REPLACE FUNCTION obter_dados_marca_material ( ds_marca_p text, cd_material_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
S	Status da marca
SD	Status da marca e Data atualizacao
D	Data atualizacao
C	CÃ³digo do status da marca
P	Padronizado
*/
nr_seq_marca_w		bigint;
ds_status_w		varchar(255) := '';
ds_status_data_w		varchar(255) := '';
ds_retorno_w		varchar(255) := '';
dt_atualizacao_w		timestamp;
nr_seq_status_aval_w	bigint;
ie_padronizado_w	material_marca.ie_padronizado%type;


BEGIN

select	coalesce(max(a.nr_sequencia),0)
into STRICT	nr_seq_marca_w
from	marca b,
	material_marca a
where	a.cd_material		= cd_material_p
and	a.nr_sequencia		= b.nr_sequencia
and	((substr(upper(substr(CASE WHEN a.cd_referencia='' THEN b.ds_marca  ELSE b.ds_marca || '(' || a.cd_referencia || ')' END ,1,255)),1,30) = substr(upper(ds_marca_p),1,30)) or (substr(upper(b.ds_marca),1,30) = substr(upper(ds_marca_p),1,30)));


if (nr_seq_marca_w > 0) then

	select	max(nr_seq_status_aval),
		substr(max(obter_desc_status_aval_marca(nr_seq_status_aval)),1,255),
		max(dt_atualizacao),
		max(coalesce(ie_padronizado,'N'))
	into STRICT	nr_seq_status_aval_w,
		ds_status_w,
		dt_atualizacao_w,
		ie_padronizado_w
	from	material_marca
	where	nr_sequencia	= nr_seq_marca_w
	and	cd_material	= cd_material_p;

	if (ds_status_w IS NOT NULL AND ds_status_w::text <> '') then
		ds_status_data_w	:= substr(ds_status_w || ' - ' || PKG_DATE_FORMATERS.TO_VARCHAR(dt_atualizacao_w, 'shortDate', WHEB_USUARIO_PCK.GET_CD_ESTABELECIMENTO, WHEB_USUARIO_PCK.GET_NM_USUARIO),1,255);
	end if;

end if;

if (ie_opcao_p = 'S') then
	ds_retorno_w := ds_status_w;
elsif (ie_opcao_p = 'D') then
	ds_retorno_w := PKG_DATE_FORMATERS.TO_VARCHAR(dt_atualizacao_w, 'shortDate', WHEB_USUARIO_PCK.GET_CD_ESTABELECIMENTO, WHEB_USUARIO_PCK.GET_NM_USUARIO);
elsif (ie_opcao_p = 'SD') then
	ds_retorno_w := ds_status_data_w;
elsif (ie_opcao_p = 'C') then
	ds_retorno_w := nr_seq_status_aval_w;
elsif (ie_opcao_p = 'P') then
	ds_retorno_w := ie_padronizado_w;
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
