CREATE OR REPLACE FUNCTION obter_dados_marca_mat ( cd_estabelecimento_p bigint, cd_material_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* ie_opcao_p

'RA' registro anvisa
'CF' código de referência
*/
ds_retorno_w			varchar(255)	:= null;
qt_reg_w			bigint;
nr_registro_marca_w		varchar(60)	:= null;
cd_referencia_w			varchar(80);
nr_sequencia_w			bigint;


BEGIN

if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then

	select	max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from	material_marca
	where	cd_material		= cd_material_p
	and (cd_estabelecimento	= cd_estabelecimento_p or coalesce(cd_estabelecimento::text, '') = '')
	and	(nr_sequencia IS NOT NULL AND nr_sequencia::text <> '');

		select	max(nr_registro_anvisa),
			max(cd_referencia)
		into STRICT	nr_registro_marca_w,
			cd_referencia_w
		from	material_marca
		where	nr_sequencia		= nr_sequencia_w
		and	cd_material		= cd_material_p
		AND	coalesce(IE_SITUACAO,'A') = 'A';

	/* Se ainda não encontrou, buscar na tabela material */

	if (ie_opcao_p = 'RA') then
		ds_retorno_w := nr_registro_marca_w;
	elsif (ie_opcao_p = 'CF') then
		ds_retorno_w := cd_referencia_w;
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
