CREATE OR REPLACE FUNCTION obter_dados_material_fornec ( cd_estabelecimento_p bigint, cd_cnpj_p text, cd_material_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


/*ie_opcao_p
0	CD_UNID_MEDIDA 
1	QT_CONV_COMPRA_EST
2	QT_COMPRA_MELHOR
3	CD_CGC
4	DS_FORNEC
*/
			

nr_sequencia_w		bigint;
cd_unid_medida_w	varchar(30);
qt_conv_compra_est_w	double precision;
qt_compra_melhor_w	double precision;
cd_cgc_w		varchar(14);
ds_retorno_w		varchar(255);
			

BEGIN

select	max(nr_sequencia)
into STRICT	nr_sequencia_w
from	material_fornec
where	cd_estabelecimento = cd_estabelecimento_p
and	coalesce(cd_cnpj_p,cd_cgc) = cd_cgc
and	cd_material = cd_material_p;

if (nr_Sequencia_w > 0) then
	select	cd_unid_medida,
		qt_conv_compra_est,
		qt_compra_melhor,
		cd_cgc
	into STRICT	cd_unid_medida_w,
		qt_conv_compra_est_w,
		qt_compra_melhor_w,
		cd_cgc_w
	from	material_fornec
	where	nr_Sequencia = nr_sequencia_w;
	
	if (ie_opcao_p = 0) then
		ds_retorno_w := cd_unid_medida_w;
	elsif (ie_opcao_p = 1) then
		ds_retorno_w := qt_conv_compra_est_w;
	elsif (ie_opcao_p = 2) then
		ds_retorno_w := qt_compra_melhor_w;
	elsif (ie_opcao_p = 3) then
		ds_retorno_w := cd_cgc_w;
	elsif (ie_opcao_p = 4) then
		ds_retorno_w := obter_nome_pf_pj('',cd_cgc_w);
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
