CREATE OR REPLACE FUNCTION obter_dados_material_infus ( cd_material_p bigint, ie_tipo_dado_p text) RETURNS varchar AS $body$
DECLARE


/*	CON - Concentração
	CUM - Cód unid medida concentracao
	CMB - Cód unid med base concentracao
	CUC - Cód unid med consumo
*/
qt_conversao_mg_w		double precision;
cd_unid_med_concetracao_w		varchar(30);
cd_unid_med_base_conc_w		varchar(30);
cd_unidade_medida_consumo_w	varchar(30);
ds_retorno_w			varchar(15);
cd_estabelecimento_w		smallint := wheb_usuario_pck.get_cd_estabelecimento;


BEGIN

select	qt_conversao_mg,
	cd_unid_med_concetracao,
	cd_unid_med_base_conc,
	substr(obter_dados_material_estab(cd_material,cd_estabelecimento_w,'UMS'),1,30) cd_unidade_medida_consumo
into STRICT	qt_conversao_mg_w,
	cd_unid_med_concetracao_w,
	cd_unid_med_base_conc_w,
	cd_unidade_medida_consumo_w
from 	material
where 	cd_material = cd_material_p;

if (ie_tipo_dado_p = 'CON') then
	ds_retorno_w := qt_conversao_mg_w;
elsif (ie_tipo_dado_p = 'CUM') then
	ds_retorno_w := cd_unid_med_concetracao_w;
elsif (ie_tipo_dado_p = 'CMB') then
	ds_retorno_w := cd_unid_med_base_conc_w;
elsif (ie_tipo_dado_p = 'CUC') then
	ds_retorno_w := cd_unidade_medida_consumo_w;
end if;

RETURN ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
