SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_material_prescr (nr_prescricao_p bigint, nr_seq_material_p bigint, ie_informacao_p text) RETURNS varchar AS $body$
DECLARE


vl_retorno_w		varchar(240);
qt_unitaria_w		double precision;
cd_um_dose_w		varchar(30);
qt_solucao_w		double precision;
ds_observacao_w		varchar(4000);
cd_material_w		integer;
ie_medicacao_paciente_w 	varchar(1);


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') and (ie_informacao_p IS NOT NULL AND ie_informacao_p::text <> '') then

	select	qt_unitaria,
		cd_unidade_medida_dose,
		qt_solucao,
		ds_observacao,
		cd_material,
		coalesce(ie_medicacao_paciente,'N')
	into STRICT	qt_unitaria_w,
		cd_um_dose_w,
		qt_solucao_w,
		ds_observacao_w,
		cd_material_w,
		ie_medicacao_paciente_w
	from	prescr_material
	where	nr_prescricao	= nr_prescricao_p
	and	nr_sequencia	= nr_seq_material_p;

	if (ie_informacao_p = 'QT_UNITARIA') then
		vl_retorno_w := qt_unitaria_w;
	elsif (ie_informacao_p = 'CD_UNIDADE_MEDIDA_DOSE') then
		vl_retorno_w := cd_um_dose_w;
	elsif (ie_informacao_p = 'QT_SOLUCAO') then
		vl_retorno_w := qt_solucao_w;
	elsif (ie_informacao_p = 'DS_OBSERVACAO') then
		vl_retorno_w := ds_observacao_w;
	elsif (ie_informacao_p = 'CD_MATERIAL') then
		vl_retorno_w := cd_material_w;
	elsif (ie_informacao_p = 'M') then
		vl_retorno_w := ie_medicacao_paciente_w;
	end if;

end if;

return vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
