CREATE OR REPLACE FUNCTION obter_dados_mat_conv_conta (cd_material_p bigint, ie_tipo_p text, cd_convenio_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w varchar(255);
cd_material_conta_w bigint;

/* C - CÃ³digo do material conta
*/
BEGIN

select	cd_material_conta
into STRICT	cd_material_conta_w
from	material_convenio
where	cd_convenio = cd_convenio_p
and	cd_material = cd_material_p
and	cd_estabelecimento = cd_estabelecimento_p;

if (ie_tipo_p = 'C') then
	ds_retorno_w := cd_material_conta_w;
end if;

return	ds_retorno_w;

end;
$body$
