CREATE OR REPLACE FUNCTION obter_dados_medicamento_padrao ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
TR	-	Descrição do tipo de receita
*/
ds_tipo_receita_w	varchar(255);
ds_retorno_w		varchar(255);
ie_tipo_receita_w	varchar(255);


BEGIN

if (nr_sequencia_p > 0) then
	select	substr(obter_valor_dominio(1117,ie_tipo_receita),1,255),
		ie_tipo_receita
	into STRICT	ds_tipo_receita_w,
		ie_tipo_receita_w
	from	med_medic_padrao
	where	nr_sequencia	=	nr_sequencia_p;
end if;

if (ie_opcao_p = 'TR') then
	ds_retorno_w	:= ds_tipo_receita_w;
elsif (ie_opcao_p = 'CTR') then
	ds_retorno_w	:= ie_tipo_receita_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
