CREATE OR REPLACE FUNCTION obter_dados_medico_req_parecer ( NR_ATENDIMENTO_P bigint, NR_PARECER_P bigint, IE_OPCAO_P text ) RETURNS varchar AS $body$
DECLARE


nm_medico_w  varchar(255) := '';
ds_especialidade_w  varchar(255) := '';
resultado_w  varchar(4000);

/*
NM - Nome do medico requisitante
ES - Especialidade do medico requisitante
*/
BEGIN

select distinct substr(obter_nome_pf(cd_medico),1,60) med_req,
obter_especialidade_medico(cd_medico,'DV') especialidade
into STRICT nm_medico_w,ds_especialidade_w
from
PARECER_MEDICO_REQ
WHERE NR_ATENDIMENTO = NR_ATENDIMENTO_P
and (coalesce(NR_PARECER_P::text, '') = '' OR NR_PARECER = NR_PARECER_P)
and dt_atualizacao = (SELECT max(dt_atualizacao) 
from PARECER_MEDICO_REQ
WHERE NR_ATENDIMENTO= NR_ATENDIMENTO_P);

 IF (IE_OPCAO_P = 'NM') then
  resultado_w  := nm_medico_w;
END IF;
 IF (IE_OPCAO_P = 'ES') THEN
  resultado_w  := ds_especialidade_w;
end if;

return resultado_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
