SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_medico ( CD_MEDICO_P text, IE_OPCAO_P text) RETURNS varchar AS $body$
DECLARE


nr_crm_w			varchar(20);
nm_guerra_w		varchar(60);
uf_crm_w			valor_dominio.vl_dominio%type;
ds_descricao_w		varchar(255);
ie_cobra_pf_pj_w		varchar(1);
ds_vinculo_w		varchar(240);
cd_cgc_w		varchar(14);
nr_seq_conselho_w		bigint;
nr_rqe_w			varchar(20);
nr_seq_categoria_w	bigint;
ds_categoria_w		varchar(255);
ie_retaguarda_w		varchar(01);
NR_PRESCRITOR_w		bigint;

/*
UFCRM - CRM state
IERECEB - Receivement pattern
V - Link
CRM
SGCRM - Council abbreviation
RQE
RT - Rearguard
*/
BEGIN

if (ie_opcao_p = 'SGCRM') then

	select	coalesce(max(nr_seq_conselho),0)
	into STRICT	nr_seq_conselho_w
	from	pessoa_fisica
	where	cd_pessoa_fisica	= cd_medico_p;

	select	coalesce(max(sg_conselho),'')
	into STRICT	ds_descricao_w
	from	CONSELHO_PROFISSIONAL
	where	nr_sequencia		= nr_seq_conselho_w;

else

select	substr(obter_crm_medico(cd_pessoa_fisica), 1, 255),
		nm_guerra,
		substr(obter_uf_crm_medico(cd_pessoa_fisica), 1, 255),
		coalesce(ie_cobra_pf_pj,'F'),
		substr(obter_valor_dominio(3,ie_vinculo_medico),1,240),
		cd_cgc,
		nr_rqe,
		nr_seq_categoria,
		substr(Obter_desc_categ_medico(nr_seq_categoria),1,255),
		ie_retaguarda,
		NR_PRESCRITOR
	into STRICT	nr_crm_w,
		nm_guerra_w,
		uf_crm_w,
		ie_cobra_pf_pj_w,
		ds_vinculo_w,
		cd_cgc_w,
		nr_rqe_w,
		nr_seq_categoria_w,
		ds_categoria_w,
		ie_retaguarda_w,
		NR_PRESCRITOR_w
	from	medico
	where	cd_pessoa_fisica	= CD_MEDICO_P;

	if (IE_OPCAO_P = 'UFCRM') then
		ds_descricao_w	:= uf_crm_w;
	elsif (IE_OPCAO_P = 'IERECEB') then
		ds_descricao_w	:= ie_cobra_pf_pj_w;
	elsif (IE_OPCAO_P = 'V') then
		ds_descricao_w	:= ds_vinculo_w;
	elsif (IE_OPCAO_P = 'CAT') then
		ds_descricao_w	:= nr_seq_categoria_w;
	elsif (IE_OPCAO_P = 'CRM') then
		ds_descricao_w	:= nr_crm_w;
	elsif (ie_opcao_p = 'CGC') then
		ds_descricao_w	:= cd_cgc_w;		
	elsif (ie_opcao_p = 'RQE') then
		ds_descricao_w	:= nr_rqe_w;
	elsif (ie_opcao_p = 'DSCAT') then
		ds_descricao_w	:= ds_categoria_w;
	elsif (ie_opcao_p = 'PN') then
		ds_descricao_w	:= NR_PRESCRITOR_w;
	elsif (ie_opcao_p = 'CONS') then
		select	coalesce(max(nr_seq_conselho),0)
		into STRICT	nr_seq_conselho_w
		from	pessoa_fisica
		where	cd_pessoa_fisica	= cd_medico_p;

		select	coalesce(max(sg_conselho),'')
		into STRICT	ds_descricao_w
		from	CONSELHO_PROFISSIONAL
		where	nr_sequencia		= nr_seq_conselho_w;
		
	elsif (ie_opcao_p = 'RT') then
		ds_descricao_w	:= coalesce(ie_retaguarda_w,'N');
	elsif (ie_opcao_p = 'NM') then
		ds_descricao_w	:= coalesce(trim(both nm_guerra_w),substr(obter_nome_pf_pj(cd_medico_p,null),1,255));
	end if;
end if;

RETURN ds_descricao_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_medico ( CD_MEDICO_P text, IE_OPCAO_P text) FROM PUBLIC;

