CREATE OR REPLACE FUNCTION obter_dados_medic_atb_num ( cd_material_p bigint, cd_estabelecimento_p bigint, qt_valor_atual_p bigint, ie_opcao_p text, ie_tipo_atendimento_p bigint, cd_setor_atendimento_p bigint, ie_clinica_p bigint ) RETURNS bigint AS $body$
DECLARE

/*
CM - Controle Médico
DT - Dias Terapia
DP - Dias Profilaxia
DA - Dias de Aplicação
*/
qt_retorno_w	integer;




BEGIN
if (ie_opcao_p = 'CM') then
	select	max(ie_controle_medico)
	into STRICT		qt_retorno_w
	from		rep_regra_controle_atb
	where	cd_material = cd_material_p
