	and		coalesce(cd_setor_atendimento,coalesce(cd_setor_atendimento_p,0)) = coalesce(cd_setor_atendimento_p,0)
	and		coalesce(ie_clinica,coalesce(ie_clinica_p,0)) 						= coalesce(ie_clinica_p,0);

	if (coalesce(qt_retorno_w::text, '') = '') then
		qt_retorno_w := qt_valor_atual_p;
	end if;
elsif (ie_opcao_p = 'DT') then
	select	max(qt_dia_terapeutico)
	into STRICT		qt_retorno_w
	from		rep_regra_controle_atb
	where	cd_material = cd_material_p
	and		cd_estabelecimento = cd_estabelecimento_p
	and		coalesce(ie_tipo_atendimento,coalesce(ie_tipo_atendimento_p,0))	= coalesce(ie_tipo_atendimento_p,0)
	and		coalesce(cd_setor_atendimento,coalesce(cd_setor_atendimento_p,0)) = coalesce(cd_setor_atendimento_p,0)
	and		coalesce(ie_clinica,coalesce(ie_clinica_p,0)) 						= coalesce(ie_clinica_p,0);

	if (coalesce(qt_retorno_w::text, '') = '') then
		qt_retorno_w := qt_valor_atual_p;
	end if;
elsif (ie_opcao_p = 'DP') then
	select	max(qt_dia_profilatico)
	into STRICT		qt_retorno_w
	from		rep_regra_controle_atb
	where	cd_material = cd_material_p
	and		cd_estabelecimento = cd_estabelecimento_p
	and		coalesce(ie_tipo_atendimento,coalesce(ie_tipo_atendimento_p,0))	= coalesce(ie_tipo_atendimento_p,0)
	and		coalesce(cd_setor_atendimento,coalesce(cd_setor_atendimento_p,0)) = coalesce(cd_setor_atendimento_p,0)
	and		coalesce(ie_clinica,coalesce(ie_clinica_p,0)) 						= coalesce(ie_clinica_p,0);

	if (coalesce(qt_retorno_w::text, '') = '') then
		qt_retorno_w := qt_valor_atual_p;
	end if;
elsif (ie_opcao_p = 'DA') then
	select	max(qt_dia_profilatico)
	into STRICT		qt_retorno_w
	from		rep_regra_controle_atb
	where	cd_material = cd_material_p
	and		cd_estabelecimento = cd_estabelecimento_p
	and		coalesce(ie_tipo_atendimento,coalesce(ie_tipo_atendimento_p,0))	= coalesce(ie_tipo_atendimento_p,0)
	and		coalesce(cd_setor_atendimento,coalesce(cd_setor_atendimento_p,0)) = coalesce(cd_setor_atendimento_p,0)
	and		coalesce(ie_clinica,coalesce(ie_clinica_p,0)) 						= coalesce(ie_clinica_p,0);

	if (coalesce(qt_retorno_w::text, '') = '') then
		qt_retorno_w := qt_valor_atual_p;
	end if;
end if;

return qt_retorno_w;

end;
