CREATE OR REPLACE FUNCTION obter_dados_medic_atb_var ( cd_material_p bigint, cd_estabelecimento_p bigint, ds_valor_atual_p text, ie_opcao_p text, ie_tipo_atendimento_p bigint, cd_setor_atendimento_p bigint, ie_clinica_p bigint) RETURNS varchar AS $body$
DECLARE

/*
JD - Justificativa e Dias de utilização
JP - Justificativa Padrão
OJ - Obriga Justificativa
CD - Consiste dias de utilização na REP
OB - Objetivo de Uso
*/
ds_retorno_w	varchar(255);




BEGIN
if (ie_opcao_p = 'JD') then
	select	max(ie_justifica_dias_util)
	into STRICT		ds_retorno_w
	from		rep_regra_controle_atb
	where	cd_material = cd_material_p
	and		cd_estabelecimento = cd_estabelecimento_p
	and		coalesce(ie_tipo_atendimento,coalesce(ie_tipo_atendimento_p,0))	= coalesce(ie_tipo_atendimento_p,0)
	and		coalesce(cd_setor_atendimento,coalesce(cd_setor_atendimento_p,0)) = coalesce(cd_setor_atendimento_p,0)
	and		coalesce(ie_clinica,coalesce(ie_clinica_p,0)) 						= coalesce(ie_clinica_p,0);

	if (coalesce(ds_retorno_w::text, '') = '') then
		ds_retorno_w := ds_valor_atual_p;
	end if;
elsif (ie_opcao_p = 'JP') then
	select	max(ie_justificativa_padrao)
	into STRICT		ds_retorno_w
	from		rep_regra_controle_atb
	where	cd_material = cd_material_p
	and		cd_estabelecimento = cd_estabelecimento_p
	and		coalesce(ie_tipo_atendimento,coalesce(ie_tipo_atendimento_p,0))	= coalesce(ie_tipo_atendimento_p,0)
	and		coalesce(cd_setor_atendimento,coalesce(cd_setor_atendimento_p,0)) = coalesce(cd_setor_atendimento_p,0)
	and		coalesce(ie_clinica,coalesce(ie_clinica_p,0)) 						= coalesce(ie_clinica_p,0);

	if (coalesce(ds_retorno_w::text, '') = '') then
		ds_retorno_w := ds_valor_atual_p;
	end if;
elsif (ie_opcao_p = 'OJ') then
	select	max(ie_obriga_justificativa)
	into STRICT		ds_retorno_w
	from		rep_regra_controle_atb
	where	cd_material = cd_material_p
	and		cd_estabelecimento = cd_estabelecimento_p
	and		coalesce(ie_tipo_atendimento,coalesce(ie_tipo_atendimento_p,0))	= coalesce(ie_tipo_atendimento_p,0)
	and		coalesce(cd_setor_atendimento,coalesce(cd_setor_atendimento_p,0)) = coalesce(cd_setor_atendimento_p,0)
	and		coalesce(ie_clinica,coalesce(ie_clinica_p,0)) 						= coalesce(ie_clinica_p,0);

	if (coalesce(ds_retorno_w::text, '') = '') then
		ds_retorno_w := ds_valor_atual_p;
	end if;
elsif (ie_opcao_p = 'CD') then
	select	max(ie_consiste_dias)
	into STRICT		ds_retorno_w
	from		rep_regra_controle_atb
	where	cd_material = cd_material_p
	and		cd_estabelecimento = cd_estabelecimento_p
	and		coalesce(ie_tipo_atendimento,coalesce(ie_tipo_atendimento_p,0))	= coalesce(ie_tipo_atendimento_p,0)
	and		coalesce(cd_setor_atendimento,coalesce(cd_setor_atendimento_p,0)) = coalesce(cd_setor_atendimento_p,0)
	and		coalesce(ie_clinica,coalesce(ie_clinica_p,0)) 						= coalesce(ie_clinica_p,0);

	if (coalesce(ds_retorno_w::text, '') = '') then
		ds_retorno_w := ds_valor_atual_p;
	end if;
elsif (ie_opcao_p = 'OB') then
	select	max(ie_objetivo)
	into STRICT		ds_retorno_w
	from		rep_regra_controle_atb
	where	cd_material = cd_material_p
	and		cd_estabelecimento = cd_estabelecimento_p
	and		coalesce(ie_tipo_atendimento,coalesce(ie_tipo_atendimento_p,0))	= coalesce(ie_tipo_atendimento_p,0)
	and		coalesce(cd_setor_atendimento,coalesce(cd_setor_atendimento_p,0)) = coalesce(cd_setor_atendimento_p,0)
	and		coalesce(ie_clinica,coalesce(ie_clinica_p,0)) 						= coalesce(ie_clinica_p,0);

	if (coalesce(ds_retorno_w::text, '') = '') then
		ds_retorno_w := ds_valor_atual_p;
	end if;
end if;

return ds_retorno_w;

end;
$body$
