SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_medic_quimio ( nr_seq_atendimento_p bigint, nr_seq_material_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
O	-	Observação
R	-	Recomendação
D	-	Dias aplicação
DQ	-	Dose quimioterapia
M	-	Medicamento
V	-	Via acesso
A	-	Via de aplicação
*/
ds_observacao_w		varchar(255);
ds_recomendacao_w	varchar(2000);
ds_dias_aplicacao_w varchar(4000);
ds_medicamento_w	varchar(255);
ds_dose_w			varchar(255);
ds_via_acesso_w		varchar(255);
ds_retorno_w		varchar(255);
cd_material_w		integer;
nr_seq_paciente_w	bigint;
ds_observacao_ww	varchar(255);
ds_via_aplicacao_w	varchar(255);



BEGIN

if	(nr_seq_atendimento_p > 0 AND nr_seq_material_p > 0) then

	if (ie_opcao_p = 'O') then

		select	substr(max(a.ds_observacao),1,255)
		into STRICT	ds_retorno_w
		FROM material b, paciente_atend_medic a
LEFT OUTER JOIN via_acesso c ON (a.nr_seq_via_acesso = c.nr_sequencia)
WHERE a.cd_material 		= b.cd_material  and a.nr_seq_atendimento 	= nr_seq_atendimento_p and a.nr_seq_material 	= nr_seq_material_p;

	elsif (ie_opcao_p = 'R') then

		select	substr(max(a.ds_recomendacao),1,255)
		into STRICT	ds_retorno_w
		FROM material b, paciente_atend_medic a
LEFT OUTER JOIN via_acesso c ON (a.nr_seq_via_acesso = c.nr_sequencia)
WHERE a.cd_material 		= b.cd_material  and a.nr_seq_atendimento 	= nr_seq_atendimento_p and a.nr_seq_material 	= nr_seq_material_p;

	elsif (ie_opcao_p = 'DQ') then

		select	substr(max(to_char(a.qt_dose) || ' ' || a.cd_unid_med_dose),1,255)
		into STRICT	ds_retorno_w
		FROM material b, paciente_atend_medic a
LEFT OUTER JOIN via_acesso c ON (a.nr_seq_via_acesso = c.nr_sequencia)
WHERE a.cd_material 		= b.cd_material  and a.nr_seq_atendimento 	= nr_seq_atendimento_p and a.nr_seq_material 	= nr_seq_material_p;

	elsif (ie_opcao_p = 'M') then

		select	substr(max(b.ds_reduzida),1,255)
		into STRICT	ds_retorno_w
		FROM material b, paciente_atend_medic a
LEFT OUTER JOIN via_acesso c ON (a.nr_seq_via_acesso = c.nr_sequencia)
WHERE a.cd_material 		= b.cd_material  and a.nr_seq_atendimento 	= nr_seq_atendimento_p and a.nr_seq_material 	= nr_seq_material_p;

	elsif (ie_opcao_p = 'V') then

		select	substr(max(c.ds_via_acesso),1,255)
		into STRICT	ds_retorno_w
		FROM material b, paciente_atend_medic a
LEFT OUTER JOIN via_acesso c ON (a.nr_seq_via_acesso = c.nr_sequencia)
WHERE a.cd_material 		= b.cd_material  and a.nr_seq_atendimento 	= nr_seq_atendimento_p and a.nr_seq_material 	= nr_seq_material_p;

	elsif (ie_opcao_p = 'A') then

		select	substr(max(obter_via_aplicacao(a.ie_via_aplicacao, 'D')),1,255)
		into STRICT	ds_retorno_w
		FROM material b, paciente_atend_medic a
LEFT OUTER JOIN via_acesso c ON (a.nr_seq_via_acesso = c.nr_sequencia)
WHERE a.cd_material 		= b.cd_material  and a.nr_seq_atendimento 	= nr_seq_atendimento_p and a.nr_seq_material 	= nr_seq_material_p;

	elsif (ie_opcao_p in ('D','OB')) then

		select	max(nr_seq_paciente)
		into STRICT	nr_seq_paciente_w
		from	paciente_atendimento
		where	nr_seq_atendimento 	= nr_seq_atendimento_p;

		if (ie_opcao_p = 'OB') then

			select	substr(max(ds_observacao),1,255)
			into STRICT	ds_retorno_w
			from	paciente_setor
			where	nr_seq_paciente		= nr_seq_paciente_w;

		elsif (ie_opcao_p = 'D') then

			select	cd_material
			into STRICT	cd_material_w
			from	paciente_atend_medic
			where	nr_seq_atendimento 	= nr_seq_atendimento_p
			and	nr_seq_material 	= nr_seq_material_p;

			select	substr(max(ds_dias_aplicacao),1,255)
			into STRICT	ds_retorno_w
			from	paciente_protocolo_medic
			where	nr_seq_paciente		= nr_seq_paciente_w
			and	cd_material 		= cd_material_w;

		end if;

	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_medic_quimio ( nr_seq_atendimento_p bigint, nr_seq_material_p bigint, ie_opcao_p text) FROM PUBLIC;

