CREATE OR REPLACE FUNCTION obter_dados_medic_uso_continuo (nr_seq_medicamento_p bigint, ie_opcao_p text, ie_dado_p text) RETURNS varchar AS $body$
DECLARE


qt_dose_w			varchar(15);
cd_unidade_medida_dose_w	varchar(30);
ds_prescricao_w			varchar(60);
ie_via_w			varchar(05);
ds_retorno_w			varchar(100);
nr_seq_motivo_susp_w		bigint;
nr_seq_medic_subst_w		bigint;
ds_material_w			varchar(100);
DS_MOTIVO_SUSP_w		varchar(100);


BEGIN

if (ie_opcao_p = 'N') then

	select	a.qt_dose,
		a.cd_unidade_medida,
		b.ds_prescricao,
		a.ie_via_aplicacao,
		a.nr_seq_motivo_susp,
		a.nr_seq_medic_subst
	into STRICT	qt_dose_w,
		cd_unidade_medida_dose_w,
		ds_prescricao_w,
		ie_via_w,
		nr_seq_motivo_susp_w,
		nr_seq_medic_subst_w
	FROM medic_uso_continuo a
LEFT OUTER JOIN intervalo_prescricao b ON (a.cd_intervalo = b.cd_intervalo)
WHERE a.nr_sequencia		= nr_seq_medicamento_p;

elsif (ie_opcao_p = 'H') then
	select	a.qt_dose,
		a.cd_unidade_medida,
		b.ds_prescricao,
		a.ie_via_aplicacao,
		a.nr_seq_motivo_susp,
		a.nr_seq_medic_subst
	into STRICT	qt_dose_w,
		cd_unidade_medida_dose_w,
		ds_prescricao_w,
		ie_via_w,
		nr_seq_motivo_susp_w,
		nr_seq_medic_subst_w
	FROM medic_uso_continuo_hist a
LEFT OUTER JOIN intervalo_prescricao b ON (a.cd_intervalo = b.cd_intervalo)
WHERE a.nr_sequencia		= nr_seq_medicamento_p;
end if;

if (ie_dado_p = 'D') then
	ds_retorno_w	:= qt_dose_w|| ' ' ||cd_unidade_medida_dose_w||'   '||ds_prescricao_w||'   '||ie_via_w;

elsif (ie_dado_p = 'MA') then

	select	max(substr(obter_desc_material(a.cd_material),1,100))
	into STRICT	ds_material_w
	from	medic_uso_continuo a
	where	a.nr_sequencia = nr_seq_medic_subst_w;

	ds_retorno_w	:= ds_material_w;

elsif (ie_dado_p = 'S') then

	select	max(DS_MOTIVO_SUSP)
	into STRICT	DS_MOTIVO_SUSP_w
	from	MOTIVO_SUSP_MEDIC_USO_CONT
	where 	nr_sequencia = nr_seq_motivo_susp_w;

	ds_retorno_w	:= DS_MOTIVO_SUSP_w;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
