SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_med_padrao (nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w varchar(255);

/*Opção
D  - Descrição do material
U - Unidade de medida do material*/
BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (ie_opcao_p = 'D') then

	select  max(coalesce(ds_material_selecao,ds_material))
	into STRICT ds_retorno_w
	from  med_medic_padrao
	where nr_sequencia = nr_sequencia_p;

end if;

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (ie_opcao_p = 'U') then

	select  substr(max(OBTER_DESC_UNID_MED(cd_unidade_medida_estoque)),1,200)
	into STRICT ds_retorno_w
	from  med_medic_padrao
	where nr_sequencia = nr_sequencia_p;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
