SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_mensuracao_date ( nr_seq_cp_indicator_p bigint, nr_seq_prescr_p bigint, ie_opcao_p text) RETURNS timestamp AS $body$
DECLARE


ds_retorno_w	timestamp;
nr_sequencia_w	bigint;


BEGIN

if (nr_seq_prescr_p IS NOT NULL AND nr_seq_prescr_p::text <> '') and (nr_seq_cp_indicator_p IS NOT NULL AND nr_seq_cp_indicator_p::text <> '') then

	select	max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from	patient_cp_indic_measure
	where	nr_seq_prescr 		= nr_seq_prescr_p
	and		nr_seq_cp_indicator = nr_seq_cp_indicator_p
	order 	by dt_evaluation desc;


	if (ie_opcao_p = 'DT') then
	
		Select 	a.dt_evaluation
		into STRICT	ds_retorno_w
		from 	patient_cp_indic_measure a
		where 	a.nr_sequencia = nr_sequencia_w
		and ((dt_liberacao IS NOT NULL AND dt_liberacao::text <> '') or nm_usuario = wheb_usuario_pck.get_nm_usuario);

	elsif (ie_opcao_p = 'DTL') then
	
		Select 	a.dt_liberacao
		into STRICT	ds_retorno_w
		from 	patient_cp_indic_measure a
		where 	a.nr_sequencia = nr_sequencia_w
		and ((dt_liberacao IS NOT NULL AND dt_liberacao::text <> '') or nm_usuario = wheb_usuario_pck.get_nm_usuario)
		order 	by a.dt_evaluation desc;

	elsif (ie_opcao_p = 'DTP') then
	
		Select 	a.DT_PRESCRICAO
		into STRICT	ds_retorno_w
		from 	PE_PRESCRICAO a
		where 	a.nr_sequencia = nr_seq_prescr_p
		order 	by a.DT_PRESCRICAO desc;

	elsif (ie_opcao_p = 'DTG') then
	
		select 	max(a.DT_EVALUATION_GOAL)
        into STRICT	ds_retorno_w
		from 	PATIENT_CP_INDIC_PLAN a,
				PATIENT_CP_INDIC_MEASURE b 
		where 	b.nr_seq_plan_indic = a.nr_sequencia 
		and 	b.nr_seq_cp_indicator   = nr_seq_cp_indicator_p
		and		b.nr_seq_prescr		= nr_seq_prescr_p;
			
	end if;
	
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_mensuracao_date ( nr_seq_cp_indicator_p bigint, nr_seq_prescr_p bigint, ie_opcao_p text) FROM PUBLIC;

