SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_men_vl_comp_mat ( cd_estabelecimento_p bigint, qt_dia_p bigint, cd_material_p bigint, cd_local_estoque_p bigint, ie_tipo_p text, ie_tipo_retorno_p text) RETURNS varchar AS $body$
DECLARE


/*Function para buscar dados do MENOR valor de compra de um material.*/

/* Valores ie_tipo_retorno_p

CGC	- CNPJ do fornecedor;
QT	- Quantidade compra;
UM	- Unidade medida material;
VL	- Valor unitario do item;
DT	- Data Entrada saida;

*/
vl_menor_valor_w		double precision;
vl_select_w			double precision;
nr_sequencia_w			bigint;
cd_material_estoque_w		integer;
cd_local_estoque_w		bigint;
nr_sequencia_ww			bigint;
nr_item_nf_w			integer;
nr_sequencia_www		bigint;
nr_item_nf_ww			integer;
ds_retorno_w			varchar(80);
cd_cgc_emitente_w		varchar(14);
qt_item_nf_w			double precision;
cd_unidade_medida_compra_w	varchar(30);
dt_entrada_saida_w		timestamp;


c01 CURSOR FOR
SELECT	a.nr_sequencia
into STRICT	nr_sequencia_w
from	natureza_operacao o,
	operacao_nota p,
	nota_fiscal b,
	nota_fiscal_item a
where	a.nr_sequencia			= b.nr_sequencia
and	b.cd_natureza_operacao		= o.cd_natureza_operacao
and	b.cd_operacao_nf 		= p.cd_operacao_nf
and	a.cd_estabelecimento		= cd_estabelecimento_p
and	a.dt_atualizacao		>= clock_timestamp() - coalesce(qt_dia_p,360)
and	a.cd_material  			= cd_material_p
and	b.ie_situacao			= '1'
and	o.ie_entrada_saida		= 'E'
and	p.ie_ultima_compra		= 'S'
and	((cd_local_estoque_w = 0) or
	(cd_local_estoque_p > 0 AND a.cd_local_estoque = cd_local_estoque_p));


BEGIN

cd_local_estoque_w	:= coalesce(cd_local_estoque_p,0);
vl_menor_valor_w	:= 99999999999.99;
vl_select_w		:= 0;

open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (coalesce(ie_tipo_p, 'N') = 'N') then

		select	coalesce(max(dividir((a.vl_total_item_nf - a.vl_desconto - vl_desconto_rateio + vl_frete + a.vl_despesa_acessoria + a.vl_seguro), a.qt_item_estoque)),0),
			max(nr_sequencia),
			max(nr_item_nf)
		into STRICT	vl_select_w,
			nr_sequencia_ww,
			nr_item_nf_w
		from 	nota_fiscal_item a
		where	nr_sequencia 	= nr_sequencia_w
		and	cd_material	= cd_material_p;

		if (vl_select_w < vl_menor_valor_w) and (vl_select_w > 0) then
			begin
			vl_menor_valor_w 	:= vl_select_w;
			nr_sequencia_www 	:= nr_sequencia_ww;
			nr_item_nf_ww		:= nr_item_nf_w;
			end;
		end if;

	elsif (coalesce(ie_tipo_p, 'N') <> 'N') then

		select	coalesce(max(dividir((a.vl_total_item_nf - a.vl_desconto - vl_desconto_rateio + vl_frete + a.vl_despesa_acessoria + a.vl_seguro), a.qt_item_estoque)),0),
			max(nr_sequencia),
			max(nr_item_nf)
		into STRICT	vl_select_w,
			nr_sequencia_ww,
			nr_item_nf_w
		from 	nota_fiscal_item a
		where	nr_sequencia 		= nr_sequencia_w
		and	cd_material_estoque	= cd_material_p;

		if (vl_select_w < vl_menor_valor_w) and (vl_select_w > 0) then
			begin
			vl_menor_valor_w 	:= vl_select_w;
			nr_sequencia_www 	:= nr_sequencia_ww;
			nr_item_nf_ww		:= nr_item_nf_w;
			end;
		end if;
	end if;
	end;
end loop;
close C01;

select	b.cd_cgc_emitente,
	a.qt_item_nf,
	a.cd_unidade_medida_compra,
	b.dt_entrada_saida
into STRICT	cd_cgc_emitente_w,
	qt_item_nf_w,
	cd_unidade_medida_compra_w,
	dt_entrada_saida_w
from	nota_fiscal_item a,
	nota_fiscal b
where	a.nr_sequencia = b.nr_sequencia
and	a.nr_item_nf = nr_item_nf_ww
and	b.nr_sequencia = nr_sequencia_www;

if (ie_tipo_retorno_p = 'CGC') then
	ds_retorno_w := cd_cgc_emitente_w;
elsif (ie_tipo_retorno_p = 'QT') then
	ds_retorno_w := qt_item_nf_w;
elsif (ie_tipo_retorno_p = 'UM') then
	ds_retorno_w := cd_unidade_medida_compra_w;
elsif (ie_tipo_retorno_p = 'VL') then
	ds_retorno_w := vl_menor_valor_w;
elsif (ie_tipo_retorno_p = 'DT') then
	ds_retorno_w := to_char(dt_entrada_saida_w,'dd/mm/yyyy');
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_men_vl_comp_mat ( cd_estabelecimento_p bigint, qt_dia_p bigint, cd_material_p bigint, cd_local_estoque_p bigint, ie_tipo_p text, ie_tipo_retorno_p text) FROM PUBLIC;

