CREATE OR REPLACE FUNCTION obter_dados_mif (nr_sequencia_p bigint, ie_campo_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 	varchar(400);


BEGIN

If (ie_campo_p = 'QT_LOC_CADEIRA') then
	select 	SUBSTR(CASE WHEN QT_LOC_CADEIRA=7 THEN 'Independência completa' WHEN QT_LOC_CADEIRA=6 THEN 'Independência modificada' WHEN QT_LOC_CADEIRA=5 THEN 'Supervisão; orientação ou preparo' WHEN QT_LOC_CADEIRA=4 THEN 'Ajuda mínima' WHEN QT_LOC_CADEIRA=3 THEN 'Ajuda moderada' WHEN QT_LOC_CADEIRA=2 THEN 'Ajuda máxima' WHEN QT_LOC_CADEIRA=1 THEN 'Ajuda total' END ,1,100)
	into STRICT	ds_retorno_w
	from	escala_mif
	where	nr_sequencia = nr_sequencia_p;

elsif (ie_campo_p = 'QT_ALIMENTACAO') then
	select	OBTER_DADOS_PADRAO_MIF(nr_sequencia_p, QT_ALIMENTACAO)
	into STRICT	ds_retorno_w
	from	escala_mif
