CREATE OR REPLACE FUNCTION obter_dados_modulo_implantacao ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
D - Descrição
A - Area
S - Situação
*/
ds_retorno_w	varchar(255);
ie_area_tasy_w	varchar(03);
nm_modulo_w	varchar(40);
ie_situacao_w varchar(1);

BEGIN

select	ie_area_tasy,
	nm_modulo,
	ie_situacao
into STRICT	ie_area_tasy_w,
	nm_modulo_w,
	ie_situacao_w
from	modulo_implantacao
where	nr_sequencia = nr_sequencia_p;


if (ie_opcao_p = 'D') then
	ds_retorno_w	:= nm_modulo_w;
elsif (ie_opcao_p = 'A') then
	ds_retorno_w	:= ie_area_tasy_w;
elsif (ie_opcao_p = 'S') then
	ds_retorno_w	:= ie_situacao_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
