CREATE OR REPLACE FUNCTION obter_dados_moeda ( cd_moeda_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


/*ie_opcao_p
0 - IE_CONV_COT_COMPRA
1 - CD_MOEDA_EXT*/
ie_conv_cot_compra_w		moeda.ie_conv_cot_compra%type;
cd_moeda_ext_w			moeda.cd_moeda_ext%type;
ds_retorno_w			varchar(255);


BEGIN
begin
	select	coalesce(ie_conv_cot_compra,'N'),
		cd_moeda_ext
	into STRICT	ie_conv_cot_compra_w,
		cd_moeda_ext_w
	from	moeda
	where	cd_moeda	= cd_moeda_p;
exception when others then
	ds_retorno_w	:= null;
end;

if (ie_opcao_p = 0) then
	ds_retorno_w := ie_conv_cot_compra_w;
elsif (ie_opcao_p = 1) then
	ds_retorno_w := cd_moeda_ext_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
