SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_motivo_cancel ( cd_motivo_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_motivo_w	varchar(255);
nr_seq_grupo_w	bigint;
ds_retorno_w	varchar(255);


BEGIN
if (cd_motivo_p IS NOT NULL AND cd_motivo_p::text <> '') then
	select	max(ds_motivo),
		max(nr_seq_grupo)
	into STRICT	ds_motivo_w,
		nr_seq_grupo_w
	from	agenda_motivo_cancelamento
	where	cd_motivo	=	cd_motivo_p
	and	ie_agenda in ('CI','T');
end if;

if (ie_opcao_p = 'D') then
	ds_retorno_w	:= ds_motivo_w;
elsif (ie_opcao_p = 'G') and (nr_seq_grupo_w IS NOT NULL AND nr_seq_grupo_w::text <> '') then
	select	substr(max(ds_grupo_cancel),1,255)
	into STRICT	ds_retorno_w
	from	grupo_motivo_cancel
	where	nr_sequencia = nr_seq_grupo_w;
elsif (ie_opcao_p = 'CG') and (nr_seq_grupo_w IS NOT NULL AND nr_seq_grupo_w::text <> '') then
	ds_retorno_w	:= nr_seq_grupo_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
