CREATE OR REPLACE FUNCTION obter_dados_motivo_rever_disp ( nr_sequencia_p bigint, ie_opcao_p text ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
ds_motivo_w	varchar(255);


BEGIN

if (nr_sequencia_p > 0) then
	select	ds_motivo
	into STRICT	ds_motivo_w
	from	motivo_reversao_disp
	where	nr_sequencia	=	nr_sequencia_p;
end if;

if (ie_opcao_p = 'N') then
	ds_retorno_w	:= ds_motivo_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
