CREATE OR REPLACE FUNCTION obter_dados_movto_estoque (nr_movimento_estoque_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE



cd_centro_custo_w		integer;
cd_funcao_w			integer;
cd_local_estoque_w		smallint;
cd_local_estoque_dest_w		smallint;
cd_setor_atendimento_w		integer;
nr_seq_tab_orig_w		bigint;
cd_lote_fabricacao_w		varchar(20);
dt_validade_w			timestamp;
cd_estabelecimento_w		bigint;
ds_retorno_w			varchar(255);


/* ie_opcao_p
CC - Centro custo
CF - Funcao
LE - Local estoque
LD - Local estoque destino
SA - Setor atendimento
TO - Nr seq Tab origem
DV - Data de validade
LO - Lote fabricação
CE - Código do estabelecimento
*/
BEGIN

select	cd_centro_custo,
	cd_funcao,
	cd_local_estoque,
	cd_local_estoque_destino,
	cd_setor_atendimento,
	nr_seq_tab_orig,
	cd_lote_fabricacao,
	dt_validade,
	cd_estabelecimento
into STRICT	cd_centro_custo_w,
	cd_funcao_w,
	cd_local_estoque_w,
	cd_local_estoque_dest_w,
	cd_setor_atendimento_w,
	nr_seq_tab_orig_w,
	cd_lote_fabricacao_w,
	dt_validade_w,
	cd_estabelecimento_w
from	movimento_estoque
where	nr_movimento_estoque = nr_movimento_estoque_p;


if (ie_opcao_p = 'CC') then
	ds_retorno_w := cd_centro_custo_w;
elsif (ie_opcao_p = 'CF') then
	ds_retorno_w := cd_funcao_w;
elsif (ie_opcao_p = 'LE') then
	ds_retorno_w := cd_local_estoque_w;
elsif (ie_opcao_p = 'LD') then
	ds_retorno_w := cd_local_estoque_dest_w;
elsif (ie_opcao_p = 'SA') then
	ds_retorno_w := cd_setor_atendimento_w;
elsif (ie_opcao_p = 'TO') then
	ds_retorno_w := nr_seq_tab_orig_w;
elsif (ie_opcao_p = 'LO') then
	ds_retorno_w := cd_lote_fabricacao_w;
elsif (ie_opcao_p = 'DV') then
	ds_retorno_w := to_char(dt_validade_w);
elsif (ie_opcao_p = 'CE') then
	ds_retorno_w := cd_estabelecimento_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
